function sol=bisection(f,a,b);% seeks the position where f  equals 0.% it is  assumed that f takes N arguments and is itself N dimensional  ya = feval(f,a);   yb = feval(f,b);  Nobs = size(ya,1);  if ( max(ya) > 0 ) | ( min(yb) < 0 )    error('Error bad choice for a or b')  end      while max(abs(a-b)) > 0.001    c = (a+b)./2;    yc = feval(f,c);    indx1 = yc < 0;    indx2 = -(indx1-1);    a = indx1.*c + indx2.*a;    b = indx1.*b + indx2.*c;  end    sol = (a+b)/2;  